import React, { useState, useEffect } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';

const localizer = momentLocalizer(moment);

const InteractiveCalendar = () => {
    const [events, setEvents] = useState([]);
    const [selectedEvent, setSelectedEvent] = useState(null);

    useEffect(() => {
        fetchEvents();
    }, []);

    const fetchEvents = async () => {
        try {
            const response = await fetch('/events');
            if (!response.ok) {
                throw new Error('Failed to fetch events');
            }
            const data = await response.json();
            setEvents(data.map(event => ({
                id: event.id,
                title: event.name,
                start: new Date(event.start),
                end: new Date(event.end),
                description: event.description,
                address: event.address
            })));
        } catch (error) {
            console.error('Error fetching events:', error);
        }
    };

    const handleSelect = ({ start, end }) => {
        const name = window.prompt('Enter event name:');
        if (name) {
            const newEvent = {
                name,
                start,
                end
            };
            addEvent(newEvent);
        }
    };

    const addEvent = async (newEvent) => {
        try {
            const response = await fetch('/events', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(newEvent)
            });
            if (!response.ok) {
                throw new Error('Failed to add event');
            }
            fetchEvents();
        } catch (error) {
            console.error('Error adding event:', error);
        }
    };

    // Other functions for updating and deleting events

    return (
        <div style={{ height: '600px' }}>
            <Calendar
                localizer={localizer}
                events={events}
                startAccessor="start"
                endAccessor="end"
                selectable
                resizable
                onSelectEvent={setSelectedEvent}
                onSelectSlot={handleSelect}
                // Other event handlers for updating and deleting events
                style={{ padding: '10px' }}
            />
            {selectedEvent && (
                <div>
                    <h2>Selected Event:</h2>
                    <p>Name: {selectedEvent.name}</p>
                    <p>Description: {selectedEvent.description}</p>
                    <p>Address: {selectedEvent.address}</p>
                    <p>Start: {selectedEvent.start.toString()}</p>
                    <p>End: {selectedEvent.end.toString()}</p>
                    {/* Button for updating/deleting event */}
                </div>
            )}
        </div>
    );
};

export default InteractiveCalendar;
