// src/components/InteractiveCalendar.js
import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import './InteractiveCalendar.css'; // Import the CSS file for custom styling

const InteractiveCalendar = () => {
  const [date, setDate] = useState(new Date());
  const [events, setEvents] = useState([]);

  const handleDateChange = (newDate) => {
    setDate(newDate);
  };

  const handleAddEvent = () => {
    const eventName = prompt('Enter event name:');
    if (eventName) {
      setEvents([...events, { date, eventName }]);
    }
  };

  return (
    <div className="full-screen-calendar">
      <div className="container mt-5">
        <div className="row">
          <div className="col-md-8">
            <Calendar onChange={handleDateChange} value={date} />
          </div>
          <div className="col-md-4">
            <div className="mb-3">
              <button className="btn btn-primary" onClick={handleAddEvent}>
                Add Event
              </button>
            </div>
            <ul className="list-group">
              {events.map((event, index) => (
                <li key={index} className="list-group-item">
                  <strong>{event.eventName}</strong> - {event.date.toLocaleDateString()}
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default InteractiveCalendar;
