import React, { useState, useEffect } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import axios from 'axios';


const localizer = momentLocalizer(moment);

const InteractiveCalendar = () => {
    const [events, setEvents] = useState([]);
    const [selectedEvent, setSelectedEvent] = useState(null);

    useEffect(() => {
        fetchEvents();
    }, []);

    const fetchEvents = async () => {
        try {
            const response = await axios.get('http://localhost:8800/events');
            setEvents(response.data);
        } catch (error) {
            console.error('Error fetching events:', error);
        }
    };

    // const handleSelect = ({ start, end }) => {
    //     const name = window.prompt('Enter event name:');
    //     const description = window.prompt('Enter event description:');
    //     const address = window.prompt('Enter event address:');
    //     if (name && description && address) {
    //         const newEvent = {
    //             name,
    //             description,
    //             address,
    //             start,
    //             end
    //         };
    //         addEvent(newEvent);
    //     }
    // };

    // const addEvent = async (newEvent) => {
    //     try {
    //         await axios.post("http://localhost:8800/events", newEvent);
    //         fetchEvents();
    //     } catch (error) {
    //         console.error('Error adding event:', error);
    //     } 
    // };

    // Other functions for updating and deleting events

    return (
        <div style={{ height: '600px' }}>
            <Calendar
                localizer={localizer}
                events={events}
                startAccessor="start"
                endAccessor="end"
                selectable
                resizable
                onSelectEvent={setSelectedEvent}
                // onSelectSlot={handleSelect}
                // Other event handlers for updating and deleting events
                style={{ padding: '10px' }}
            />
            {selectedEvent && (
                <div>
                    <h2>{selectedEvent.name}</h2>
                    <p>Description: {selectedEvent.desc}</p>
                    <p>Address: {selectedEvent.address}</p>
                    <p>Start: {selectedEvent.start}</p>
                    <p>End: {selectedEvent.end}</p>
                    {/* Button for updating/deleting event */}
                </div>
            )}
        </div>
    );
};

export default InteractiveCalendar;
